var Accessory = require('./BHSA.js').Accessory;
var AccessoryLoader = require('./BHSAL.js');
var Bridge = require('./BHSB.js').Bridge;
var Camera = require('./BHSC.js').Camera;
var Characteristic = require('./BHSCH.js').Characteristic;
var HomeKitTypes = require('./BHSHKT');
var HAPServer = require('./BHSHS').HAPServer;
var Service = require('./BHSS.js').Service;
var StreamController = require('./BHSSC.js').StreamController;
var storage = require('node-persist');
var uuid = require('./util/uuid');

function Start(storagePath) {
  if (typeof storagePath !== 'undefined')
    storage.initSync({ dir: storagePath });
  else
    storage.initSync();
}

module.exports = {
  Start: Start,
  Accessory: Accessory,
  Bridge: Bridge,
  Camera: Camera,
  Service: Service,
  Characteristic: Characteristic,
  uuid: uuid,
  AccessoryLoader: AccessoryLoader,
  StreamController: StreamController,
  HAPServer: HAPServer
}