var path = require('path');
var fs = require('fs');
var os = require('os');

'use strict';

var config;
var customStoragePath;

function User() {
}

User.config = function() {
  return config || (config = Config.load(User.configPath()));
}

User.storagePath = function() {
  if (customStoragePath) return customStoragePath;
  return path.join(os.homedir(), ".bioxhome");
}

User.configPath = function() {
  return path.join(User.storagePath(), "server.json");
}

User.persistPath = function() {
  return path.join(User.storagePath(), "bhpfiles");
}

User.cachedAccessoryPath = function() {
  return path.join(User.storagePath(), "bhafiles");
}

User.setStoragePath = function(path) {
  customStoragePath = path;
}

module.exports = {
  User: User
}